import { Component, Input, OnInit, ChangeDetectionStrategy, ChangeDetectorRef, signal, inject } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FamilyMemberService } from 'src/app/services/familyMember.service';
import { FamilyMember } from 'src/app/models/FamilyMember.model';
import {
  MatSnackBar,
  MatSnackBarHorizontalPosition,
  MatSnackBarVerticalPosition,
} from '@angular/material/snack-bar';

@Component({
  selector: 'app-view',
  templateUrl: './view.component.html',
  //changeDetection: ChangeDetectionStrategy.OnPush,
  styleUrls: ['./view.component.css']
})
export class ViewComponent implements OnInit {
  id!: string;
  familyMember?: FamilyMember;
  pageMode!: string;
  submitted!: boolean;
  readonly panelOpenState = signal(false);
  isLoading = true;  // Track the loading state
  loggedInRole = '';
  loggedInMemberId = 0;
  private _snackBar = inject(MatSnackBar);
  horizontalPosition: MatSnackBarHorizontalPosition = 'center';
  verticalPosition: MatSnackBarVerticalPosition = 'top';


  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private familyMemberService: FamilyMemberService,
    //private changeDetectorRef: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    // Retrieve the id from the route parameters
    this.id = this.route.snapshot.paramMap.get('id')!;
    this.loggedInRole = this.familyMemberService.getLoggedInRole();
    this.loggedInMemberId = this.familyMemberService.getLoggedInMemberId();
    this.loadData(this.id); // Call a method to load data based on the id
  }

  loadData(id: string): void {
    this.isLoading = true;  // Show progress bar
    this.familyMemberService.getFamilyByMemberId(id).subscribe({
      next: (data) => {
        this.familyMember = data;
        this.isLoading = false;
        //console.log(data);
      },
      error: (e) => {
        console.error(e);
        this.isLoading = false;  // Hide progress bar on error
      }
    });
  }

  onEditMember(memberId: string) {
    this.pageMode = 'edit';
    // Navigate to the view component and pass the id
    this.router.navigate(['/create-update-member', {id: memberId, pageMode: this.pageMode}]);
  }

  onUpdateFamilyHead(memberId: string){
    this.familyMemberService.updateFamilyHead(memberId).subscribe({
      next: (res) => {
        console.log(res);
        this.openSnackBarWithDuration('Success: Family Head updated successfully.', 'Close');
        this.loadData(memberId);
        //this.changeDetectorRef.markForCheck();
      },
      error: (e) => console.error(e)
    });
  }

  onApproveStatus(memberId: string){
    this.familyMemberService.approveStatus(memberId).subscribe({
      next: (res) => {
        console.log(res);
        this.openSnackBarWithDuration('Success: Member approved successfully.', 'Close');
        this.loadData(memberId);
        //this.changeDetectorRef.markForCheck();
      },
      error: (e) => console.error(e)
    });
  }

  getAge(dateOfBirth: string): string {
    if (!dateOfBirth) {
      return '00'; // Handle undefined dates
    }

    const today = new Date();
    const birthDate = new Date(dateOfBirth);
    let age = today.getFullYear() - birthDate.getFullYear();
    const monthDifference = today.getMonth() - birthDate.getMonth();

    // Adjust age if birth month/day hasn't passed yet this year
    if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < birthDate.getDate())) {
      age--;
    }

    // Return age as a string, pad with '0' if the age is a single digit
    return age < 10 ? `0${age}` : `${age}`;
  }

  isSelfEdit(memberId: any): any {
    if(this.familyMember != undefined && this.familyMember.members != undefined){
      for(let i=0; i < this.familyMember?.members.length; i++){
        if(this.loggedInRole === 'selfedit' && this.loggedInMemberId === this.familyMember.members[i].memberId){
          return true;
        }
      }
    }
    return false;
  }

  checkRoles(): any {
    if(this.loggedInRole === 'admin' ||  this.loggedInRole === 'edit'){
      return true;
    }
    return false;
  }

  checkAdminRole(): any {
    if(this.loggedInRole === 'admin'){
      return true;
    }
    return false;
  }

  openSnackBar(message: string, action: string) {
    this._snackBar.open(message, action, {
      horizontalPosition: this.horizontalPosition,
      verticalPosition: this.verticalPosition
    });
  }

  openSnackBarWithDuration(message: string, action: string) {
    this._snackBar.open(message, action, {
      horizontalPosition: this.horizontalPosition,
      verticalPosition: this.verticalPosition,
      duration: 8 * 1000
    });
  }

  onRoleChange(event: any, memberId: any): void {
    console.log('Role changed to: ', event.value);
    var role = event.value;
    this.familyMemberService.updateRole(memberId, role).subscribe({
      next: (res) => {
        console.log(res);
        this.openSnackBarWithDuration('Success: Member role updated successfully.', 'Close');
        this.loadData(memberId);
        //this.changeDetectorRef.markForCheck();
      },
      error: (e) => console.error(e)
    });
  }
}
<div class="loading-container" *ngIf="isLoading">
    <!-- <mat-progress-bar mode="indeterminate"></mat-progress-bar> -->
    <mat-spinner></mat-spinner>
</div>
<div *ngIf="!isLoading">
    <div *ngFor="let member of familyMember?.members">
        <div *ngIf="member.familyHead===true" class="family-member-card">
            <span class="secondary-button-style align-row-single-line width-webkit"><span><h2>{{member.firstName}} {{member.lastName}}</h2></span>
                    <span><h2>Family Head</h2></span>
                </span>
            <div class="card-wrapper">
                    <div class="triangle">
                        <span class="age-number" *ngIf="member.dateOfBirth">{{ getAge(member.dateOfBirth) }}</span>
                        <span class="age-number" *ngIf="!member.dateOfBirth">N/A</span>
                    </div>
                    <span class="align-row-left-right card-layout">
                        <span class="img-padding">
                            <img class="rounded-photo" mat-card-lg-image *ngIf="member.photo" [src]="'data:image/jpeg;base64,' + member.photo" alt="Photo" width="200" height="200">
                        </span>
                        <span class="details-style">
                            <mat-accordion class="accordian-section">
                                <mat-expansion-panel (opened)="panelOpenState.set(true)" (closed)="panelOpenState.set(false)">
                                <mat-expansion-panel-header>
                                    <mat-panel-title class="secondary-button-style"> Personal Details </mat-panel-title>
                                    <mat-panel-description>
                                        <span></span>
                                    </mat-panel-description>
                                </mat-expansion-panel-header>
                                <div class="section Personal">
                                    <p><strong>Father's Name:</strong> {{member.fatherName}}</p>
                                    <p><strong>Date Of Birth:</strong> {{member.dateOfBirth}}</p>
                                    <p><strong>Gender:</strong> {{member.gender}}</p>
                                    <p><strong>Marital Status:</strong> {{member.maritalStatus}}</p>
                                    <p><strong>Blood Group:</strong> {{member.bloodGroup}}</p>
                                    <p><strong>Education:</strong> {{member.education}}</p>
                                    </div>
                                </mat-expansion-panel>
                            </mat-accordion>

                            <!-- <div class="section Personal">
                            <h3>Personal Details</h3>
                            <p><strong>Father's Name:</strong> {{member.fatherName}}</p>
                            <p><strong>Marital Status:</strong> {{member.maritalStatus}}</p>
                            <p><strong>Blood Group:</strong> {{member.bloodGroup}}</p>
                            <p><strong>Education:</strong> {{member.education}}</p>
                            </div> -->

                            <mat-accordion class="accordian-section">
                                <mat-expansion-panel (opened)="panelOpenState.set(true)" (closed)="panelOpenState.set(false)">
                                <mat-expansion-panel-header>
                                    <mat-panel-title class="secondary-button-style"> Demographics</mat-panel-title>
                                    <mat-panel-description>
                                        <span></span>
                                    </mat-panel-description>
                                </mat-expansion-panel-header>
                                <div class="section Personal">
                                    <p><strong>Aborigine:</strong> {{member.aborigine}}</p>
                                    <p><strong>Area:</strong> {{member.area}}</p>
                                    <p><strong>Permanent Address:</strong> {{member.permanentAddress}}</p>
                                    <p><strong>Current Address:</strong> {{member.currentAddress}}</p>
                                </div>
                                </mat-expansion-panel>
                            </mat-accordion>

                            <!-- <div class="section demographics">
                            <h3>Demographics</h3>
                            <p><strong>Aborigine:</strong> {{member.aborigine}}</p>
                            <p><strong>Area:</strong> {{member.area}}</p>
                            <p><strong>Permanent Address:</strong> {{member.permanentAddress}}</p>
                            <p><strong>Current Address:</strong> {{member.currentAddress}}</p>
                            </div> -->

                            <mat-accordion class="accordian-section">
                                <mat-expansion-panel (opened)="panelOpenState.set(true)" (closed)="panelOpenState.set(false)">
                                <mat-expansion-panel-header>
                                    <mat-panel-title class="secondary-button-style"> Professional Details</mat-panel-title>
                                    <mat-panel-description>
                                        <span></span>
                                    </mat-panel-description>
                                </mat-expansion-panel-header>
                                <div class="section Personal">
                                    <p><strong>Profession:</strong> {{member.profession}}</p>
                                    <p><strong>Profession Address:</strong> {{member.professionAddress}}</p>
                                    <p><strong>Profession Number:</strong> {{member.professionNumber}}</p>
                                    <p><strong>Profession Email:</strong> {{member.professionEmail}}</p>
                                </div>
                                </mat-expansion-panel>
                            </mat-accordion>

                            <!-- <div class="section profession">
                            <h3>Professional Details</h3>
                            <p><strong>Profession:</strong> {{member.profession}}</p>
                            <p><strong>Profession Address:</strong> {{member.professionAddress}}</p>
                            <p><strong>Profession Number:</strong> {{member.professionNumber}}</p>
                            <p><strong>Profession Email:</strong> {{member.professionEmail}}</p>
                            </div> -->

                            <mat-accordion class="accordian-section">
                                <mat-expansion-panel (opened)="panelOpenState.set(true)" (closed)="panelOpenState.set(false)">
                                <mat-expansion-panel-header>
                                    <mat-panel-title class="secondary-button-style"> Contact Details</mat-panel-title>
                                    <mat-panel-description>
                                        <span></span>
                                    </mat-panel-description>
                                </mat-expansion-panel-header>
                                <div class="section Personal">
                                    <p><strong>Primary Number:</strong> {{member.mobile}}</p>
                                    <p><strong>Alternate Number:</strong> {{member.altMobile}}</p>
                                    <p><strong>WhatsApp Number:</strong> {{member.whatsappMobile}}</p>
                                    <p><strong>Email:</strong> {{member.email}}</p>
                                </div>
                                </mat-expansion-panel>
                            </mat-accordion>

                            <!-- <div class="section contact">
                            <h3>Contact Details</h3>
                            <p><strong>Primary Number:</strong> {{member.mobile}}</p>
                            <p><strong>Alternate Number:</strong> {{member.altMobile}}</p>
                            <p><strong>WhatsApp Number:</strong> {{member.whatsappMobile}}</p>
                            <p><strong>Email:</strong> {{member.email}}</p>
                            </div> -->
                        </span>
                    </span>
                </div>
            <br>
            <mat-card class="action-card" appearance="outlined">
                <mat-card-content>
                    <span class="action-button-row">
                        <button *ngIf="checkRoles() || isSelfEdit(member.memberId)" class="primary-button-style" mat-raised-button color="secondary" (click)="onEditMember(member.memberId)" mat-button>Edit</button>
                        &nbsp;&nbsp;
                        <!-- <button class="primary-button-style" mat-raised-button color="primary" (click)="onUpdateFamilyHead(member.memberId)" mat-button>Make Family Head</button>
                        &nbsp; &nbsp; -->
                        <button *ngIf="checkRoles()" class="delete-button-style" mat-raised-button color="secondary" mat-button>Delete</button>
                        &nbsp;&nbsp;
                        <button class="approve-button-style" *ngIf="member.status==='Pending' && checkRoles()" mat-raised-button color="secondary" (click)="onApproveStatus(member.memberId)" mat-button>Approve</button>
                    </span>
                    <br>
                    <span>
                        <mat-button-toggle-group *ngIf="checkAdminRole()"
                            class="mat-button-group-layout"
                            name="roles"
                            aria-label="Roles"
                            [value]="member.role"
                            (change)="onRoleChange($event, member.memberId)">
                            <mat-button-toggle value="admin">Admin</mat-button-toggle>
