import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../services/authService.service';
import { FamilyMemberService } from '../services/familyMember.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';
  errorMessage: string = '';
  loginResponse = {
    message: '',
    role: '',
    memberId: 0,
    token: ''
  }

  constructor(private authService: AuthService, 
    private router: Router,
    private familyMemberService: FamilyMemberService) {}
  loginData = { username: '', password: '' };
  onLogin() {
    this.authService.login(this.loginData).subscribe(
      response => {
        this.loginResponse = {
          message: response.message,
          role: response.role,
          memberId: response.memberId,
          token: response.token
        }
        this.familyMemberService.setLoginResponse(this.loginResponse);
        // Handle login success, e.g., store the token and redirect
        localStorage.setItem('token', response.token);
        this.router.navigate(['/dashboard']); // Redirect to dashboard or protected route
      },
      error => {
        // Handle login error
        this.errorMessage = 'Invalid Credentials!'
        console.error('Login failed:', error);
      }
    );
  }
}
