import { Component, Input, ElementRef, ViewChild } from '@angular/core';
import html2canvas from 'html2canvas';
import jsPDF from 'jspdf';

@Component({
  selector: 'app-id-card',
  templateUrl: './id-card.component.html',
  styleUrls: ['./id-card.component.css']
})
export class IdCardComponent {
  @Input() member: any;
  @ViewChild('idCard') idCardRef!: ElementRef;

  downloadPDF() {
    const card = this.idCardRef.nativeElement;
    html2canvas(card, { scale: 3 }).then(canvas => {
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('l', 'mm', [canvas.width / 3, canvas.height / 3]); // landscape orientation
      pdf.addImage(imgData, 'PNG', 0, 0, canvas.width / 3, canvas.height / 3);
      pdf.save(`${this.member?.firstName || 'id'}-card.pdf`);
    });
  }
}
