    if (fileInput.files && fileInput.files[0]) {
      this.selectedFile = fileInput.files[0];
      if (this.selectedFile.size > this.maxFileSize) {
        this.compressImage(this.selectedFile);
      } else {
        this.convertToBase64(this.selectedFile);
      }
    }
  }
