  checkRoles(): any {
    if(this.pageMode === 'edit'){
      if((this.loggedInRole === 'admin' ||  this.loggedInRole === 'edit') || this.isSelfEdit(this.familyMember.memberId)){
        return true;
      }
    }

    if(this.pageMode === 'create'){
      if(this.loggedInRole === 'admin' ||  this.loggedInRole === 'edit'){
        return true;
      }
    }
    return false;
  }

  ngAfterViewInit(): void {
    const input = this.searchInput.nativeElement as HTMLInputElement;
    const autocomplete = new google.maps.places.Autocomplete(input);
    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place.geometry && place.geometry.location) {
        const lat = place.geometry.location.lat();
        const lng = place.geometry.location.lng();
        this.markerPosition = { lat, lng };
        this.familyMember.latitude = lat;
        this.familyMember.longitude = lng;
      }
    });
  }

  onMapClick(event: google.maps.MapMouseEvent) {
    if (event.latLng) {
      const lat = event.latLng.lat();
      const lng = event.latLng.lng();
      this.markerPosition = { lat, lng };
      this.familyMember.latitude = lat;
      this.familyMember.longitude = lng;
    }
  }

  onMarkerDrag(_: any, marker: any) {
    const position = marker.getPosition();
    if (position) {
      const lat = position.lat();
      const lng = position.lng();
      this.markerPosition = { lat, lng };
      this.familyMember.latitude = lat;
      this.familyMember.longitude = lng;
    }
  }

  getCurrentLocation() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((position) => {
        const lat = position.coords.latitude;
        const lng = position.coords.longitude;
        this.mapCenter = { lat, lng };
        this.markerPosition = { lat, lng };
        this.familyMember.latitude = lat;
        this.familyMember.longitude = lng;
      }, (error) => {
        console.error("Geolocation error:", error);
        this.openSnackBar('Location access denied or unavailable.', 'Close');
      });
    } else {
      this.openSnackBar('Geolocation not supported by this browser.', 'Close');
    }
  }

  preventSubmit(event: Event) {
    event.preventDefault();
  }
