class ViewComponent {
  constructor(route, router, familyMemberService) {
    this.route = route;
    this.router = router;
    this.familyMemberService = familyMemberService;
    this.panelOpenState = signal(false);
    this.isLoading = true; // Track the loading state
    this.loggedInRole = '';
    this.loggedInMemberId = 0;
    this._snackBar = inject(MatSnackBar);
    this.horizontalPosition = 'center';
    this.verticalPosition = 'top';
  }
