import { Component, Injectable, HostListener, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FamilyMemberService } from 'src/app/services/familyMember.service';
import { AuthService } from './services/authService.service';

@Injectable({
  providedIn: 'root'
})
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'Sadhumargi Sangh Directory';
  loggedInMemberId = 0;

  constructor(private router: Router,
    private authService: AuthService,
    private familyMemberService: FamilyMemberService
  ) {
    this.setViewportHeight();
    this.addEventListeners();
  }

  ngOnInit(): void {
    this.loggedInMemberId = this.familyMemberService.getLoggedInMemberId();
  }

  // Function to dynamically calculate and set the viewport height
  setViewportHeight(): void {
    const vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', `${vh}px`);
  }

  // Function to listen for resize and load events
  addEventListeners(): void {
    window.addEventListener('resize', () => this.setViewportHeight());
    window.addEventListener('load', () => this.setViewportHeight());
  }

  logout(): void {
    this.authService.logout(); // Call the AuthService to perform logout
    this.router.navigate(['/login']); // Redirect to the login page
  }

  isLoggedOut() {
    if(this.authService.isAuthenticated()){
      return true;
    }
    return false;
  }

  viewMyProfile() {
      if(this.loggedInMemberId !== 0){
        // Navigate to the view component and pass the id
      this.router.navigate(['/view', this.loggedInMemberId]);
      }
  }

  allSearch(){
    this.router.navigate(['/allsearch']);
  }
}
<div class="page-container">
  <nav class="navbar navbar-expand navbar-dark primary-button-style">
    <section>
      <!-- <div class="example-label">Mini FAB</div> -->
      <div class="example-button-row">
        <div class="example-flex-container">
          <div class="example-button-container">
            <button mat-mini-fab aria-label="Example icon button with a menu icon">
              <mat-icon>menu</mat-icon>
            </button>
          </div>
          <!-- <div class="example-button-container">
            <button mat-mini-fab disabled aria-label="Example icon button with a home icon">
              <mat-icon>home</mat-icon>
            </button>
          </div> -->
        </div>
      </div>
    </section>
    <div class="navbar-brand font-color-primary sjs-layout">SJS</div>
    <div class="navbar-nav mr-auto">
      <li class="nav-item">
        <a routerLink="dashboard" class="nav-link font-color-primary">FAMILIES</a>
      </li>
      <li class="nav-item">
        <button class="font-color-primary" mat-button (click)="viewMyProfile()">MY FAMILY</button>
      </li>
      <li class="nav-item">
        <button class="font-color-primary" mat-button (click)="allSearch()">SEARCH</button>
      </li>
      <!-- <li class="nav-item">
        <a routerLink="add" class="nav-link">ADD MEMBER</a>
      </li> -->
    </div>
    <div>
      <button *ngIf="isLoggedOut()" class="font-color-primary" mat-button (click)="logout()">Logout</button>
    </div>
  </nav>
  <div class="container mt-3 app-style">
  <!-- <div> -->
    <router-outlet></router-outlet>
  </div>
</div>
