import {AfterViewInit, Component, ViewChild} from '@angular/core';
import {MatPaginator, MatPaginatorModule} from '@angular/material/paginator';
import {MatSort, MatSortModule} from '@angular/material/sort';
import {MatTableDataSource, MatTableModule} from '@angular/material/table';
import { TutorialService } from 'src/app/services/tutorial.service';
import { Tutorial } from 'src/app/models/tutorial.model';
import {MatInputModule} from '@angular/material/input';
import {MatFormFieldModule} from '@angular/material/form-field';

//import { UntypedFormBuilder, UntypedFormGroup } from '@angular/forms';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
  searchQuery: string = '';
  tutorials?: Tutorial[];;
  firstName: string = '';
  lastName: string = '';
  mobile: string = '';
  fatherName: string = '';
  area: string = '';
  dataSource!: MatTableDataSource<Tutorial>;
  displayedColumns: string[] = ['id', 'title', 'description', 'officeAddress', 'published'];

  @ViewChild(MatPaginator)
  paginator!: MatPaginator;
  @ViewChild(MatSort)
  sort!: MatSort;
  constructor(
    //private formBuilder: UntypedFormBuilder
    private tutorialService: TutorialService
  ) {}

  ngOnInIt(): void{
  }

  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
    this.dataSource.sort = this.sort;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();

    if (this.dataSource.paginator) {
      this.dataSource.paginator.firstPage();
    }
  }

  onSearch() {
    // Implement your search logic here
    // This is just a placeholder for demonstration
    console.log("first name value :: "+this.firstName);
    console.log("first name value :: "+this.lastName);
    this.tutorialService.findByTitle(this.firstName).subscribe({
      next: (data) => {
        this.tutorials = data;
        console.log(data);
      },
      error: (e) => console.error(e)
    });

  }
}
